import { useState } from 'react';
import { useAppSelector } from '../../../../hooks/hooks';
import { COMMENTS_ON_PAGE } from '../../../../const';
import { Comments } from '../../../../types/data-types';
import CommentItem from '../comment-item/comment-item';

function CommentsList (): JSX.Element {

  const { comments } = useAppSelector(({PRODUCT}) => PRODUCT);

  const [showComments, setShowComments] = useState <Comments> (comments.sort((a, b) => b.createAt - a.createAt).slice(0, COMMENTS_ON_PAGE));


  return (
    <section className="reviews">
      <h3 className="reviews__title title title--bigger">Отзывы</h3>
      <span className="button button--red-border button--big reviews__sumbit-button">Оставить отзыв</span>
      {showComments.map((item) =>(
        <CommentItem key={item.id} comment={item} />
      ))}
      <button
        className="button button--medium reviews__more-button"
        onClick={(evt) => {
          evt.preventDefault();
          setShowComments(comments);
        }}
      >
        Показать еще отзывы
      </button>
      <a className="button button--up button--red-border button--big reviews__up-button" href="#header">Наверх</a>
    </section>
  );
}

export default CommentsList;
