import { useState, useEffect } from 'react';
import { Link, useParams, Navigate } from 'react-router-dom';
import { store } from '../../store/store';
import { fetchGuitarsRangeActions } from '../../store/api-actions';
import { getPages, setPages } from '../../store/catalog-process/catalog-process';
import { useAppSelector, useAppDispatch } from '../../hooks/hooks';
import Header from '../header/header';
import Footer from '../footer/footer';
import CatalogFilter from './components/catalog-filter/catalog-filter';
import CatalogSort from './components/catalog-sort/catalog-sort';
import GuitarCard from './components/guitar-card/gutar-card';
import Pagination from './components/pagination/pagination';
import CartAddPopup from '../cart-add-popup/cart-add-popup';
import CartAddSuccess from '../cart-add-success/cart-add-success';

import { CARDS_PER_PAGE, AppRoute  } from '../../const';


function Catalog(): JSX.Element {
  const params = useParams();

  const dispatch = useAppDispatch();
  const { guitars, currentPage, pages } = useAppSelector(({CATALOG}) => CATALOG);



  const [selectGuitarId, setSelectGuitarId] = useState <null | number> (null);
  const [addToCart, setAddToCart] = useState <boolean> (false);

  const start = CARDS_PER_PAGE*(currentPage-1);
  const end = CARDS_PER_PAGE*currentPage;

  dispatch(setPages(guitars.length/CARDS_PER_PAGE));
  dispatch(getPages());

  const { guitarsRange } = useAppSelector(({CATALOG}) => CATALOG);

  useEffect(() => {
    store.dispatch(fetchGuitarsRangeActions({start, end}));
  }, [start, end]);
  
  if(Number(params.id) > pages){
    return (<Navigate to={'/*'} />);
  }

  return (
    <div className="wrapper">
      <Header />
      <main className="page-content">
        <div className="container">
          <h1 className="page-content__title title title--bigger">Каталог гитар</h1>
          <ul className="breadcrumbs page-content__breadcrumbs">
            <li className="breadcrumbs__item">
              <Link to={AppRoute.Index} className="link">Главная</Link>
            </li>
            <li className="breadcrumbs__item">
              <span className="link">Каталог</span>
            </li>
          </ul>
          <div className="catalog">
            <CatalogFilter />
            <CatalogSort />
            <div className="cards catalog__cards">
              {
                guitarsRange.map((item) => <GuitarCard key={item.id} guitar={item} onGuitarId={(id) => setSelectGuitarId(id)}  />)
              }
            </div>
            <Pagination />
          </div>
        </div>
      </main>
      <Footer />
      {selectGuitarId===null? null : <CartAddPopup guitar={guitarsRange.filter((item) => item.id === selectGuitarId)[0]} onGuitarId={(id) => setSelectGuitarId(id)} onAddSuccess={(value: boolean) => setAddToCart(value)} />}
      {addToCart && <CartAddSuccess onAddSuccess={(value: boolean) => setAddToCart(value)} />}
    </div>
  );
}

export default Catalog;
