import { useState } from 'react';
import { useParams } from 'react-router-dom';
import { useAppSelector } from '../../../../hooks/hooks';
import { COMMENTS_ON_PAGE } from '../../../../const';
import { Comments, Comment } from '../../../../types/data-types';
import CommentItem from '../comment-item/comment-item';
import NewCommentPopup from '../new-comment-popup/new-comment-popup';
import SuccessCommentAddPopup from '../success-comment-add-popup/success-comment-add-popup';

function CommentsList (): JSX.Element {

  const { guitar } = useAppSelector(({PRODUCT}) => PRODUCT);
  const [showComments, setShowComments] = useState <Comments> (guitar.comments.slice(0, COMMENTS_ON_PAGE));
  const [newComment, setNewComment] = useState <boolean> (false);
  const [successAddComment, setSuccessAddComment] = useState <boolean> (false);


  return (
    <>
      <section className="reviews">
        <h3 className="reviews__title title title--bigger">Отзывы</h3>
        <span 
          className="button button--red-border button--big reviews__sumbit-button"
          onClick={() => {
            setNewComment(true);
          }}
        >
          Оставить отзыв
        </span>
        {showComments.map((item) =>(
          <CommentItem key={item.id} comment={item} />
        ))}
        <button
          className="button button--medium reviews__more-button"
          onClick={(evt) => {
            evt.preventDefault();
            setShowComments(guitar.comments);
          }}
        >
        Показать еще отзывы
        </button>
        <a className="button button--up button--red-border button--big reviews__up-button" href="#header">Наверх</a>
      </section>
      {newComment && <NewCommentPopup id={guitar.id as number} onNewComment={(value:boolean) => setNewComment(value)} />}
      {successAddComment && <SuccessCommentAddPopup />}
    </>
  );
}

export default CommentsList;
